          <option>Keyboard</option>
          <option>Drummer</option>
          <option>Bassist</option>
          <option>Drummer</option>
        </select>
        <button className="role-selector-button">Add</button>
      </div>
      <div className="songs">
        <label>Song 1: </label>
        <input onChange={(element) => setPostData({ ...postData, song1: element.target.value })} />
        <label>Song 2: </label>
        <input onChange={(element) => setPostData({ ...postData, song2: element.target.value })} />
