import axios from "axios";

import { useState, useEffect } from "react";
import "./styles.css";

const PostForm = () => {
  const [members, setMembers] = useState([]);
  const [postData, setPostData] = useState({
    author: "",
    singer1: "",
    singer2: "",
    keyboard: "",
    electric: "",
    acoustic: "",
    bass: "",
    drummer: "",
    song1: "",
    song2: "",
    song3: "",
    song4: "",
  });

  useEffect(() => {
    axios.get("http://localhost:5000/members").then((response) => {
      setMembers(response.data);
    });
  }, []);

  const handleSubmit = () => {
    axios.post("http://localhost:5000/posts", postData);
    console.log(postData);
  };

  const options = members.map((member) => {
    return <option value={member.firstName}>{member.firstName + " " + member.lastName}</option>;
  });

  return (
    <div className="form">
      <div className="lineup">
        <label>Singer: </label>
        <select className="member-selector" onChange={(e) => setPostData({ ...postData, singer1: e.target.value })}>
          {options}
        </select>
        <label>Singer: </label>
        <select className="member-selector" onChange={(e) => setPostData({ ...postData, singer2: e.target.value })}>
          {options}
        </select>
        <label>Keyboard: </label>
        <select className="member-selector" onChange={(e) => setPostData({ ...postData, keyboard: e.target.value })}>
          {options}
        </select>
        <label>Electric: </label>
        <select className="member-selector" onChange={(e) => setPostData({ ...postData, electric: e.target.value })}>
          {options}
        </select>
        <label>Acoustic: </label>
        <select className="member-selector" onChange={(e) => setPostData({ ...postData, acoustic: e.target.value })}>
          {options}
        </select>
        <label>Bass: </label>
        <select className="member-selector" onChange={(e) => setPostData({ ...postData, bass: e.target.value })}>
          {options}
        </select>
        <label>Drummer: </label>
        <select className="member-selector" onChange={(e) => setPostData({ ...postData, drummer: e.target.value })}>
          {options}
        </select>
      </div>
      <div className="songs">
        <label>Song 1: </label>
        <input onChange={(element) => setPostData({ ...postData, song1: element.target.value })} />
        <label>Song 2: </label>
        <input onChange={(element) => setPostData({ ...postData, song2: element.target.value })} />
        <label>Song 3: </label>
        <input onChange={(element) => setPostData({ ...postData, song3: element.target.value })} />
        <label>Song 4: </label>
        <input onChange={(element) => setPostData({ ...postData, song4: element.target.value })} />
      </div>
      <button className="post" onClick={handleSubmit}>
        Post Lineup
      </button>
    </div>
  );
};

export default PostForm;
