    author: "",
    singer1: "",
    singer2: "",
    keyboard: "",
    electric: "",
    acoustic: "",
    bass: "",
    drummer: "",
    song1: "",
    song2: "",
    song3: "",
    song4: "",
  });

  useEffect(() => {
    axios.get("http://localhost:5000/members").then((response) => {
      setMembers(response.data);
    });
  }, []);

  const handleSubmit = () => {
    axios.post("http://localhost:5000/posts", postData);
    console.log(postData);
  };

  const handleAddRole = () => {
    setRoles([...roles, <option>add</option>])
