const Post = ({ post, members }) => {
  const lineupElements = post.assignments.map((assignment, i) => {
    // const roleElements = ;

    return (
      <div key={post._id + "role" + i}>
        {assignment.role + ": "}
        {() => {
          for (let i = 0; i < assignment.members.length; i++) {
            let member = members.find((member) => assignment.members[i] === member._id);
            return member.firstName;
          }
        }}
      </div>
    );
  });
  const songElements = post.songs.map((song, i) => {
    return <div key={post._id + ":song:" + i}>{song.title}</div>;
  });

  return (
    <div className="post">
      <div className="lineup">
        Lineup:
        {lineupElements}
      </div>
      <div className="songs">
        Songs:
        {songElements}
      </div>
    </div>
  );
};

export default Post;
