import axios from "axios";

import { useState, useEffect } from "react";
import "./styles.css";

const PostForm = () => {
  const [role, setRole] = useState();
  const [roles, setRoles] = useState([]);
  const [members, setMembers] = useState([<option></option>]);
  const [postData, setPostData] = useState({ author: "", roles: {}, songs: {} });

  useEffect(() => {
    axios.get("http://localhost:5000/members").then((response) => {
      setMembers(response.data);
    });
  }, []);

  const handleSubmitPost = () => {
    axios.post("http://localhost:5000/posts", postData);
    console.log(postData);
  };

  const handleAddRole = () => {
    setRoles([...roles, role]);
    // console.log();
  };

  const handleRemoveRole = () => {
    console.log(postData.roles);
  };

  const memberOptionsElements = members.map((member) => {
    return <option value={member.firstName}>{member.firstName + " " + member.lastName}</option>;
  });

  const roleSelectorElements = roles.map((role) => {
    return (
      <div className="">
        <label>{role}: </label>
        <select
          className="member-selector"
          onChange={(e) => setPostData({ ...postData, roles: { ...postData.roles, singer: e.target.value } })}
        >
          <option>select a member</option>
          {memberOptionsElements}
        </select>
        <button onClick={handleRemoveRole}>remove</button>
      </div>
    );
  });

  return (
    <div className="post-form">
      <div className="lineup">
        {roleSelectorElements}
        <div className="add-role-selector">
          <select className="add-role-selector-options" onChange={(e) => setRole(e.target.value)}>
            <option>select a role</option>
            <option value="Vocal">Vocal</option>
            <option value="Acoustic Guitar">Acoustic Guitar</option>
            <option value="Keyboard">Keyboard</option>
            <option value="Electic Guitar">Electric Guitar</option>
            <option value="Bass">Bass</option>
            <option value="Drummer">Drummer</option>
          </select>
          <button className="add-role-selector-button" onClick={handleAddRole}>
            Add
          </button>
        </div>
      </div>
      <div className="songs">
        <label>Song 1: </label>
        <input
          onChange={(element) =>
            setPostData({ ...postData, songs: { ...postData.songs, song1: element.target.value } })
          }
        />
        <label>Song 2: </label>
        <input
          onChange={(element) =>
            setPostData({ ...postData, songs: { ...postData.songs, song2: element.target.value } })
          }
        />
        <label>Song 3: </label>
        <input
          onChange={(element) =>
            setPostData({ ...postData, songs: { ...postData.songs, song3: element.target.value } })
          }
        />
        <label>Song 4: </label>
        <input
          onChange={(element) =>
            setPostData({ ...postData, songs: { ...postData.songs, song4: element.target.value } })
          }
        />
      </div>
      <button className="submit-post-button" onClick={handleSubmitPost}>
        Post Lineup
      </button>
    </div>
  );
};

export default PostForm;
