const Post = ({ post, members }) => {
  const lineupElements = post.assignments.map((assignment, i) => {
    return (
      <div key={post._id + "role" + i}>
        {assignment.role + ": "}
        {assignment.members.map((member) => {
          let m = members.find((m) => m._id === member);
          if (m) return <div>{m.firstName}</div>;
        })}
      </div>
    );
  });

  const songElements = post.songs.map((song, i) => {
    return (
      <li key={post._id + ":song:" + i} className="post-song">
        {song.title}
      </li>
    );
  });

  return (
    <div className="post">
      <div className="post-lineup">
        <h4>Lineup:</h4>
        <div>{lineupElements}</div>
      </div>
      <div className="post-songs">
        Songs:
        <ol className="post-songs-list">{songElements}</ol>
      </div>
    </div>
  );
};

export default Post;
