import axios from "axios";

import { useState, useEffect } from "react";
import "./styles.css";

const PostForm = () => {
  const [role, setRole] = useState();
  const [roles, setRoles] = useState([]);
  const [members, setMembers] = useState([]);
  const [postData, setPostData] = useState({
    author: "",
    roles: {},
    song1: "",
    song2: "",
    song3: "",
    song4: "",
  });

  useEffect(() => {
    axios.get("http://localhost:5000/members").then((response) => {
      setMembers(response.data);
    });
  }, []);

  const handleSubmit = () => {
    axios.post("http://localhost:5000/posts", postData);
    console.log(postData);
  };

  const handleAddRole = () => {
    setRoles([...roles, role]);
    // console.log();
  };

  const memberOptions = members.map((member) => {
    return <option value={member.firstName}>{member.firstName + " " + member.lastName}</option>;
  });

  const notagoodname = roles.map((role) => {
    return (
      <div className="">
        <label>{role}</label>
          <select className="member-selector" onChange={(e) => setPostData({ ...postData, singer1: e.target.value })}>
            {memberOptions}
          </select>
      </div>
    );
  });

  return (
    <div className="form">
      <div className="lineup">
        {notagoodname}
        <select className="role-selector-options" onChange={(e) => setRole(e.target.value)}>
          <option>select a role</option>
          <option value="vocal">Vocal</option>
          <option value="acoustic">Acoustic Guitar</option>
          <option value="keyboard">Keyboard</option>
          <option value="electric">Electric Guitar</option>
          <option value="bass">Bass</option>
          <option value="drummer">Drummer</option>
        </select>
        <button className="role-selector-button" onClick={handleAddRole}>
          Add
        </button>
      </div>
      <div className="songs">
        <label>Song 1: </label>
        <input onChange={(element) => setPostData({ ...postData, song1: element.target.value })} />
        <label>Song 2: </label>
        <input onChange={(element) => setPostData({ ...postData, song2: element.target.value })} />
        <label>Song 3: </label>
        <input onChange={(element) => setPostData({ ...postData, song3: element.target.value })} />
        <label>Song 4: </label>
        <input onChange={(element) => setPostData({ ...postData, song4: element.target.value })} />
      </div>
      <button className="post" onClick={handleSubmit}>
        Post Lineup
      </button>
    </div>
  );
};

export default PostForm;
