import axios from "axios";

import { useState, useEffect } from "react";
import "./styles.css";

const ROLES = ["Vocal", "Acoustic Guitar", "Keyboard", "Bass", "Drummer", "Electric Guitar"];

const PostForm = () => {
  const [assignments, setAssignments] = useState({});
  const [members, setMembers] = useState([]);
  const [postData, setPostData] = useState({ author: "", roles: {}, songs: {} });

  useEffect(() => {
    axios.get("http://localhost:5000/members").then((response) => {
      setMembers(response.data);
    });
  }, []);

  const handleSubmitPost = () => {
    //setPostData()

    axios.post("http://localhost:5000/posts", postData);
    console.log(postData);
  };

  const memberOptions = members.map((member) => {
    return <option value={member.firstName}>{member.firstName + " " + member.lastName}</option>;
  });

  const roleOptions = ROLES.map((role) => {
    return <option value={role}>{role}</option>;
  });

  const addRoleOption = (role) => {
    if (assignments.hasOwnProperty(role)) {
      //existing property, add member selector as second
    } else {
      //new property, create property
      let newRoles = { ...assignments };
      newRoles[role] = [""];
      //let newRoles = roles;
      //newRoles[role] = [];
      setAssignments(newRoles);
    }
  };

  const temp = () => {
    console.log(Object.keys(assignments));
  };

  const roleSelectorElements = Object.keys(assignments).map((role) => {
    console.log(assignments.role);
    if (role.length === 1)
      return (
        <div className="role-selector" key={role + ":0"}>
          <label>{role}: </label>
          <select className="member-selector">
            <option>select a member</option>
            {memberOptions}
          </select>
          <button>remove</button>
        </div>
      );
    else
      for (let i = 0; i < role.length; i++) {
        return (
          <div className="role-selector" key={role + ":" + i}>
            <label>
              {role} #{i + 1}:{" "}
            </label>
            <select className="member-selector">
              <option>select a member</option>
              {memberOptions}
            </select>
            <button>remove</button>
          </div>
        );
      }
  });

  return (
    <div className="post-form">
      <div className="lineup">
        {roleSelectorElements}
        <div className="add-role">
          <select id="add-role-selector" className="add-role-selector">
            <option>select a role</option>
            {roleOptions}
          </select>
          <button
            className="add-role-button"
            onClick={() => addRoleOption(document.getElementById("add-role-selector").value)}
          >
            Add
          </button>
        </div>
      </div>

      <div className="songs">
        <label>Song 1: </label>
        <input onChange={temp} />
        <label>Song 2: </label>
        <input onChange={temp} />
        <label>Song 3: </label>
        <input onChange={temp} />
        <label>Song 4: </label>
        <input onChange={temp} />
      </div>

      <button className="submit-post-button" onClick={temp}>
        Post Lineup
      </button>
      <button className="submit-post-button" onClick={temp}>
        Debug
      </button>
    </div>
  );
};

export default PostForm;
