import axios from "axios";

import { useState, useEffect } from "react";
import "./styles.css";

const Form = () => {
  const [members, setMembers] = useState([]);
  const [postData, setPostData] = useState({
    author: "",
    singer1: "",
    singer2: "",
    keyboard: "",
    electric: "",
    acoustic: "",
    bass: "",
    drummer: "",
    song1: "",
    song2: "",
    song3: "",
    song4: "",
  });

  useEffect(() => {
    axios.get("http://localhost:5000/members").then((response) => {
      setMembers(response.data);
    });
    console.log(options);
  }, []);

  const handleSubmit = () => {
    axios.post("http://localhost:5000/posts", postData);
    console.log(postData);
  };

  const options = members
    .map((member) => {
      return <option value={member.firstName}>{member.firstName + " " + member.lastName}</option>;
    }).push(<div></div>);

  return (
    <div className="form">
      <div className="lineup">
        <label>Singer: </label>
        <select
          className="member-selector"
          onChange={(element) => setPostData({ ...postData, singer1: element.target.value })}
        >
          {options}
        </select>
        <label>Singer: </label>
        <input onChange={(element) => setPostData({ ...postData, singer2: element.target.value })} />
        <label>Keyboard: </label>
        <input onChange={(element) => setPostData({ ...postData, keyboard: element.target.value })} />
        <label>Electric: </label>
        <input onChange={(element) => setPostData({ ...postData, electric: element.target.value })} />
        <label>Acoustic: </label>
        <input onChange={(element) => setPostData({ ...postData, acoustic: element.target.value })} />
        <label>Bass: </label>
        <input onChange={(element) => setPostData({ ...postData, bass: element.target.value })} />
        <label>Drummer: </label>
        <input onChange={(element) => setPostData({ ...postData, drummer: element.target.value })} />
      </div>
      <div className="songs">
        <label>Song 1: </label>
        <input onChange={(element) => setPostData({ ...postData, song1: element.target.value })} />
        <label>Song 2: </label>
        <input onChange={(element) => setPostData({ ...postData, song2: element.target.value })} />
        <label>Song 3: </label>
        <input onChange={(element) => setPostData({ ...postData, song3: element.target.value })} />
        <label>Song 4: </label>
        <input onChange={(element) => setPostData({ ...postData, song4: element.target.value })} />
      </div>
      <button className="post" onClick={handleSubmit}>
        Post Lineup
      </button>
    </div>
  );
};

export default Form;
