import axios from "axios";

import { useState, useEffect } from "react";
import "./styles.css";

const Form = () => {
  const [members, setMembers] = useState([]);
  const [postData, setPostData] = useState({
    author: "",
    singer1: "",
    singer2: "",
    keyboard: "",
    electric: "",
    acoustic: "",
    bass: "",
    drummer: "",
    song1: "",
    song2: "",
    song3: "",
    song4: "",
  });

  useEffect(() => {
    axios.get("http://localhost:5000/members").then((response) => {
      setMembers(response.data);
    });
  }, []);

  const handleSubmit = () => {
    axios.post("http://localhost:5000/posts", postData);
    console.log(postData);
  };

  const memberSelector = (
    <select className="member-selector">
      <option name="default">select member</option>
      {members.map((member) => {
        return <option value={member.firstName}>{member.firstName + " " + member.lastName}</option>;
      })}
    </select>
  );

  return (
    <div className="form">
      <div className="lineup">
        <label>Singer: </label>
        {memberSelector}
        <label>Singer: </label>
        {memberSelector}
        <label>Keyboard: </label>
        {memberSelector}
        <label>Electric: </label>
        {memberSelector}
        <label>Acoustic: </label>
        {memberSelector}
        <label>Bass: </label>
        {memberSelector}
        <label>Drummer: </label>
        {memberSelector}
      </div>
      <div className="songs">
        <label>Song 1: </label>
        <input onChange={(element) => setPostData({ ...postData, song1: element.target.value })} />
        <label>Song 2: </label>
        <input onChange={(element) => setPostData({ ...postData, song2: element.target.value })} />
        <label>Song 3: </label>
        <input onChange={(element) => setPostData({ ...postData, song3: element.target.value })} />
        <label>Song 4: </label>
        <input onChange={(element) => setPostData({ ...postData, song4: element.target.value })} />
      </div>
      <button className="post" onClick={handleSubmit}>
        Post Lineup
      </button>
    </div>
  );
};

export default Form;
