import axios from "axios";

import { useState, useEffect } from "react";
import "./styles.css";

const PostForm = () => {
  // const [role, setRole] = useState();
  const [roles, setRoles] = useState([]);
  const [members, setMembers] = useState([]);
  const [postData, setPostData] = useState({ author: "", roles: {}, songs: {} });

  useEffect(() => {
    axios.get("http://localhost:5000/members").then((response) => {
      setMembers(response.data);
    });
  }, []);

  const handleSubmitPost = () => {
    //setPostData()

    axios.post("http://localhost:5000/posts", postData);
    console.log(postData);
  };

  const memberOptions = members.map((member) => {
    return <option value={member.firstName}>{member.firstName + " " + member.lastName}</option>;
  });

  const addRoleOption = (role) => {
    if (roles.some((a) => a["role"] === role)) {

    } else {
      roles.push({
        role: role,
        members: []
      })
    }
  };

  const temp = () => {
    console.log(Object.keys(roles));
  };

  // const roleSelectorElements = {
  //   return (
  //     <div className="role-selector" key={role}>
  //       <label>{role}: </label>
  //       <select className="member-selector">
  //         <option>select a member</option>
  //         {memberOptions}
  //       </select>
  //       <button>remove</button>
  //     </div>
  //   );
  // });

  return (
    <div className="post-form">
      <div className="lineup">
        {roleSelectorElements}
        <div className="add-role-selector">
          <select id="add-role-selector-options" className="add-role-selector-options" onChange={temp}>
            <option>select a role</option>
            <option value="Vocal">Vocal</option>
            <option value="Acoustic Guitar">Acoustic Guitar</option>
            <option value="Keyboard">Keyboard</option>
            <option value="Electic Guitar">Electric Guitar</option>
            <option value="Bass">Bass</option>
            <option value="Drummer">Drummer</option>
          </select>
          <button
            className="add-role-selector-button"
            onClick={() => addRoleOption(document.getElementById("add-role-selector-options").value)}
          >
            Add
          </button>
        </div>
      </div>
      <div className="songs">
        <label>Song 1: </label>
        <input onChange={temp} />
        <label>Song 2: </label>
        <input onChange={temp} />
        <label>Song 3: </label>
        <input onChange={temp} />
        <label>Song 4: </label>
        <input onChange={temp} />
      </div>
      <button className="submit-post-button" onClick={temp}>
        Post Lineup
      </button>
      <button className="submit-post-button" onClick={temp}>
        Debug
      </button>
    </div>
  );
};

export default PostForm;
