import axios from "axios";

import { useState, useEffect } from "react";
import "./styles.css";

const ROLES = ["Leader", "Vocal", "Acoustic Guitar", "Keyboard", "Bass", "Drummer", "Electric Guitar"];

const PostForm = () => {
  const [assignments, setAssignments] = useState([{ role: "Leader", members: [""] }]);
  const [members, setMembers] = useState([]);
  const [postData, setPostData] = useState({ author: "", assignments: {}, songs: {} });

  useEffect(() => {
    axios.get("http://localhost:5000/members").then((response) => {
      setMembers(response.data);
    });
  }, []);

  const handleSubmitPost = () => {
    let finalAssignments = { ...assignments };
    setPostData({ ...postData, assignments: finalAssignments });
    axios.post("http://localhost:5000/posts", postData);
  };

  const memberOptions = members.map((member) => {
    return <option value={member.firstName}>{member.firstName + " " + member.lastName}</option>;
  });

  const roleOptions = ROLES.map((role) => {
    return <option value={role}>{role}</option>;
  });

  const addRoleOption = (role) => {
    let newAssignments = [...assignments];

    if (
      newAssignments.find((role) => {
        return true;
      })
    ) {
      //existing property, add member selector as second
      newAssignments.find((role) => {
        role.members.push("");
      });
      console.log(newAssignments);
    } else {
      //new property, create property

      newAssignments.push({
        role: role,
        members: [""],
      });
      setAssignments(newAssignments);
    }
  };

  const temp = () => {
    console.log(assignments);
  };

  const roleSelectorElements = assignments.map((assignment) => {
    const elements = [];
    // if (assignment.members.length === 1) {
    //   return (
    //     <div className="role-selector" key={assignment.role + ":0"}>
    //       <label>{assignment.role}: </label>
    //       <select className="member-selector">
    //         <option>select a member</option>
    //         {memberOptions}
    //       </select>
    //       <button>remove</button>
    //     </div>
    //   );
    // } else {
    //   for (let i = 0; i < assignment.members.length; i++) {
    //     elements.push(
    //       <div className="role-selector" key={assignment.role + ":" + i}>
    //         <label>
    //           {assignment.role} #{i + 1}:{" "}
    //         </label>
    //         <select className="member-selector">
    //           <option>select a member</option>
    //           {memberOptions}
    //         </select>
    //         <button>remove</button>
    //       </div>
    //     );
    //   }
    //   return elements;
    // }
  });

  return (
    <div className="post-form">
      <div className="lineup">
        {roleSelectorElements}
        <div className="add-role">
          <select id="add-role-selector" className="add-role-selector">
            <option>select a role</option>
            {roleOptions}
          </select>
          <button
            className="add-role-button"
            onClick={() => addRoleOption(document.getElementById("add-role-selector").value)}
          >
            Add
          </button>
        </div>
      </div>

      <div className="songs">
        <label>Song 1: </label>
        <input onChange={temp} />
        <label>Song 2: </label>
        <input onChange={temp} />
        <label>Song 3: </label>
        <input onChange={temp} />
        <label>Song 4: </label>
        <input onChange={temp} />
      </div>

      <button className="submit-post-button" onClick={handleSubmitPost}>
        Post Lineup
      </button>
    </div>
  );
};

export default PostForm;
